         [773] = \"Inscription\",\n        },\n    },\n}\nlocal languageOverrideOptions = {\n    [1] = nil,\n    [2] = \"en\",\n}\n\nlocal interactables = {\n    -- Flask of Solemn Night\n    [\"105117\"] = {\n        classes={4},\n        professions={171},\n        result={\n            en=\"KILL Boss at 25%\",\n            de=\"TTE den Boss bei 25%\",\n        },\n    },\n    -- Arcane Power Conduit\n    [\"105157\"] = {\n        races={7,9},\n        professions={202},\n        result={\n            en=\"debuff Guardian Constructs, stuns for 5 seconds every 20 seconds\",\n            de=\"Wchterkonstrukte schwchen, betubt alle 20 Sekunden fr 5 Sekunden\",\n        },\n    },\n    -- Fel Orb\n    [\"105160\"] = {\n        classes={2,5,9,12},\n        result={\n            en=\"+10% Critical Strike Chance\",\n            de=\"+10% kritische Trefferchance\",\n        }, \n    },\n    -- Discarded Junk\n    [\"105215\"] = {\n        classes={3},\n        professions={164},\n        result={\n            en=\"pulls a lieutenant, 10% less health and 25% more damage for 30 seconds\",\n            de=\"zieht einen Leutnant, 10% weniger Gesundheit und 25% mehr Schaden fr 30 Sekunden\",\n        },\n    },\n    -- Nightshade Refreshments\n    [\"105249\"] = {\n        races={24,25,26},\n        professions={182,185},\n        result={\n            en=\"+10% Health\",\n            de=\"+10% Gesundheit\",\n        },\n    },\n    -- Umbral Bloom\n    [\"105340\"] = {\n        classes={11},\n        professions={182,185},\n        result={\n            en=\"+10% Haste\",\n            de=\"+10% Tempo\",\n        },\n    },\n    -- Infernal Tome\n    [\"105831\"] = {\n        classes={2,5,12},\n        result={\n            en=\"10% Damage Reduction\",\n            de=\"10% Schadensreduzierung\",\n        },\n    },\n    -- Bazaar Goods\n    [\"106018\"] = {\n        classes={1,4},\n        professions={165},\n        result={\n            en=\"pulls a lieutenant\",\n            de=\"zieht einen Leutnant\"\n        },\n    },\n    -- Magical Lantern\n    [\"106024\"] = {\n        races={4,10,27,29},\n        classes={8,13},\n        professions={333},\n        result={\n            en=\"+10% Damage Buff\",\n            de=\"+10% Schadensbuff\",\n        },\n    },\n    -- Starlight Rose Brew\n    [\"106108\"] = {\n        classes={6,10},\n        result={\n            en=\"+3% Health Regeneration & +2% Mana Regeneration\",\n            de=\"+3% Gesundheitsregeneration und +2% Manaregeneration\",\n        },\n    },\n    -- Waterlogged Scroll\n    [\"106110\"] = {\n        classes={7},\n        professions={393,773},\n        result={\n            en=\"+35% Movement Speed\",\n            de=\"+35% Bewegungsgeschwindigkeit\",\n        },\n    },\n    -- Wounded Nightborne Civilian\n    [\"106112\"] = {\n        professions={197},\n        roles={\"HEALER\"},\n        result={\n            en=\"pulls a lieutenant\",\n            de=\"zieht einen Leutnant\",\n        },\n    },\n    -- Lifesized Nightborne Statue\n    [\"106113\"] = {\n        professions={186,755},\n        result={\n            en=\"pulls a lieutenant\",\n            de=\"zieht einen Leutnant\",\n        },\n    },\n}\n\nlocal interactables_announced = {}\nlocal interactables_rt_index = 1\n\nlocal config = aura_env.config\n\nlocal function ResetInteractables()\n    interactables_announced = {}\n    interactables_rt_index = 1\n    print(\"WeakAura \\\"Court of Stars Helper\\\" interactable object data has been reset\")\nend\naura_env.CourtOfStarsHelper_ResetInteractables = ResetInteractables\n\nlocal function GetLanguageOverride()\n    local languageOverrideIndex = config.announce_language\n    local languageOverride = languageOverrideOptions[languageOverrideIndex]\n    return languageOverride\nend\n\nlocal function GetRaceName(raceID)\n    local languageOverride = GetLanguageOverride()\n    if not languageOverride then\n        local raceInfo = C_CreatureInfo.GetRaceInfo(raceID)\n        local raceName = raceInfo.raceName\n        return raceName\n    end\n    local override = languageOverrides[languageOverride]\n    local races = override[\"races\"]\n    local raceName = races[raceID]\n    return raceName\nend\n\nlocal function GetClassName(classID)\n    local languageOverride = GetLanguageOverride()\n    if not languageOverride then\n        local classInfo = C_CreatureInfo.GetClassInfo(classID)\n        local className = classInfo.className\n        return className\n    end\n    local override = languageOverrides[languageOverride]\n    local classes = override[\"classes\"]\n    local className = classes[classID]\n    return className\nend\n\nlocal function GetProfessionName(professionID)\n    local languageOverride = GetLanguageOverride()\n    if not languageOverride then\n        local professionInfo = C_TradeSkillUI.GetProfessionInfoBySkillLineID(professionID)\n        local professionName = professionInfo.professionName\n        return professionName\n    end\n    local override = languageOverrides[languageOverride]\n    local professions = override[\"professions\"]\n    local professionName = professions[professionID]\n    return professionName\nend\n\nlocal function AppendPart(source, part)\n    if string.find(source, part) then\n        return source\n    end\n    if #source > 0 then\n        source = source .. \", \"\n    end\n    source = source .. part\n    return source\nend\n\nlocal function ClassColoredText(text, classID)\n    local classInfo = C_CreatureInfo.GetClassInfo(classID)\n    local classFile = classInfo.classFile\n    local classColorData = (CUSTOM_CLASS_COLORS or RAID_CLASS_COLORS)[classFile]\n    local colorStr = classColorData.colorStr\n    local coloredText = (\"|c%s%s|r\"):format(colorStr, text)\n    return coloredText\nend\n\nlocal function CreateDetails(interactable, color)\n    local races = interactable[\"races\"] or {}\n    local classes = interactable[\"classes\"] or {}\n    local professions = interactable[\"professions\"] or {}\n    local roles = interactable[\"roles\"] or {}\n    local results = interactable[\"result\"]\n    \n    local details = \"\"\n    for _, raceID in pairs(races) do\n        local raceName = GetRaceName(raceID)\n        details = AppendPart(details, raceName)\n    end\n    for _, classID in pairs(classes) do\n        local className = GetClassName(classID)\n        if color then\n            local coloredName = ClassColoredText(className, classID)\n            className = coloredName\n        end\n        details = AppendPart(details, className)\n    end\n    for _, professionID in pairs(professions) do\n        local professionName = GetProfessionName(professionID)\n        details = AppendPart(details, professionName)\n    end\n    for _, role in pairs(roles) do\n        if role == \"TANK\" then\n            details = AppendPart(details, \"Tank\")\n        elseif role == \"HEALER\" then\n            details = AppendPart(details, \"Healer\")\n        elseif role == \"DAMAGER\" then\n            details = AppendPart(details, \"Damager\")\n        end\n    end\n    \n    local language = GetLanguageOverride()\n    if not language then\n        local locale = GetLocale()\n        language = string.sub(locale, 1, 2)\n    end\n    local resultLoc = results[language]\n    if not resultLoc then\n        resultLoc = results[\"en\"]\n    end\n    details = details .. \" [\" .. resultLoc .. \"]\"\n    return details\nend\n\nlocal function HasValue(table, val)\n    for _, v in pairs(table) do\n        if v == val then \n            return true\n        end\n    end\n    return false\nend\n\nlocal function CanInteractWith(interactable, playerLocation)\n    local races = interactable[\"races\"] or {}\n    local classes = interactable[\"classes\"] or {}\n    \n    local _, _, classID = C_PlayerInfo.GetClass(playerLocation)\n    local raceID = C_PlayerInfo.GetRace(playerLocation)\n    \n    local classMatch = HasValue(classes, classID)\n    local raceMatch = HasValue(races, raceID) or false\n    return classMatch or raceMatch\nend\n\nlocal function CanPlayerInteractWith(interactable)\n    local playerLocation = PlayerLocation:CreateFromUnit(\"player\")\n    local canInteractWith = CanInteractWith(interactable, playerLocation)\n    if canInteractWith then\n        return true\n    end\n    \n    local professions = interactable[\"professions\"] or {}\n    local roles = interactable[\"roles\"] or {}\n    \n    local profession1ID, profession2ID, _, _, cookingID = GetProfessions()\n    local profession1 = 0\n    if profession1ID then\n        _, _, _, _, _, _, profession1 = GetProfessionInfo(profession1ID)\n    end\n    local profession2 = 0\n    if profession2ID then\n        _, _, _, _, _, _, profession2 = GetProfessionInfo(profession2ID)\n    end\n    local cooking = 0\n    if cookingID then\n        _, _, _, _, _, _, cooking = GetProfessionInfo(cookingID)\n    end\n    local specIndex = GetSpecialization()\n    local _, _, _, _, role = GetSpecializationInfo(specIndex)\n    \n    local professionMatch = HasValue(professions, profession1) or HasValue(professions, profession2) or HasValue(professions, cooking) or false\n    local roleMatch = HasValue(roles, role) or false\n    \n    return professionMatch or roleMatch\nend\n\nlocal function GetPartyMembersThatCanInteractWith(interactable, color)\n    local partyMembersThatCanInteract = \"\"\n    for unit in WA_IterateGroupMembers() do\n        local canInteractWith\n        local playerLocation = PlayerLocation:CreateFromUnit(unit)\n        if unit == \"player\" then\n            canInteractWith = CanPlayerInteractWith(interactable)\n        else\n            canInteractWith = CanInteractWith(interactable, playerLocation)\n        end\n        if canInteractWith then\n            local name = C_PlayerInfo.GetName(playerLocation)\n            if color then\n                local _, _, classID = C_PlayerInfo.GetClass(playerLocation)\n                local coloredName = ClassColoredText(name, classID)\n                name = coloredName\n            end\n            partyMembersThatCanInteract = AppendPart(partyMembersThatCanInteract, name)\n        end\n    end\n    return partyMembersThatCanInteract\nend\n\nlocal function AddTooltipLines(tooltip, interactable)\n    local tooltipDetails = CreateDetails(interactable, true)\n    tooltip:AddLine(tooltipDetails, 1, 1, 1, true)\n    local canInteract = CanPlayerInteractWith(interactable) or false\n    if canInteract then\n        tooltip:AddLine(\"\\nYou can interact with this.\", 0, 1, 0, true)\n    else\n        tooltip:AddLine(\"\\nYou cannot interact with this.\", 1, 0, 0, true)\n    end\n    local partyMembersThatCanInteract = GetPartyMembersThatCanInteractWith(interactable, true)\n    if #partyMembersThatCanInteract > 1 then\n        tooltip:AddLine(\"Party members that can interact with this: \" .. partyMembersThatCanInteract, 1, 1, 1, true)\n    end\nend\n\nlocal function IsRaidTargetMarkerInUse(index)\n    for unit in WA_IterateGroupMembers() do\n        local raidTargetIndex = GetRaidTargetIndex(unit)\n        if raidTargetIndex == index then\n            return true\n        end\n    end\n    return false\nend\n\nlocal function IncreaseRaidTargetIndex(index)\n    local newIndex = index + 1\n    while IsRaidTargetMarkerInUse(newIndex) do\n        newIndex = newIndex + 1\n        if newIndex == 9 then\n            newIndex = 1\n        end\n        if newIndex == index then\n            -- this is to prevent an infinite loop\n            return\n        end\n    end\n    interactables_rt_index = newIndex\nend\n\nlocal function OnTooltip(self)\n    local _, unit = self:GetUnit()\n    if not unit then\n        return\n    end\n    local guid = UnitGUID(unit)\n    if not guid then\n        return\n    end\n    local _, _, _, _, _, npc_id = strsplit(\"-\", guid)\n    local interactable = interactables[npc_id]\n    if not interactable then\n        return\n    end\n    AddTooltipLines(self, interactable)\n    local announced = interactables_announced[npc_id] or false\n    local announce_enabled = config.announce_interactables\n    if (announce_enabled and not announced) or IsShiftKeyDown() then\n        local chatType = \"SAY\"\n        if IsInLFGDungeon() then\n            chatType = \"INSTANCE_CHAT\"\n        elseif IsInGroup() then\n            chatType = \"PARTY\"\n        end\n        local current_rt_index = GetRaidTargetIndex(unit)\n        local mark_enabled = config.mark_interactables\n        if mark_enabled then\n            if current_rt_index == nil then\n                current_rt_index = interactables_rt_index\n                IncreaseRaidTargetIndex(interactables_rt_index)\n                SetRaidTarget(unit, current_rt_index)\n            elseif interactables_rt_index <= current_rt_index then\n                IncreaseRaidTargetIndex(current_rt_index)\n            end\n        end\n        \n        local name = GetUnitName(unit)\n        local languageOverride = GetLanguageOverride()\n        if languageOverride then\n            local override = languageOverrides[languageOverride]\n            local names = override[\"names\"]\n            local overrideName = names[npc_id]\n            if overrideName then\n                name = overrideName\n            end\n        end\n        local message = name .. \": \"\n        if current_rt_index ~= nil then\n            message = message .. \"{rt\" .. current_rt_index .. \"} \"\n        end\n        local messageDetails = CreateDetails(interactable, false)\n        message = message .. messageDetails\n        SendChatMessage(message, chatType)\n        interactables_announced[npc_id] = true\n    end\nend\n\nlocal isHooked = aura_env.region.TooltipPostCallHooked or false\nif not isHooked then\n    local AddTooltipPostCall = TooltipDataProcessor and TooltipDataProcessor.AddTooltipPostCall\n    if AddTooltipPostCall then\n        AddTooltipPostCall(Enum.TooltipDataType.Unit, OnTooltip)\n        aura_env.region.TooltipPostCallHooked = true\n    else\n        print(\"WeakAura \\\"Court of Stars Helper\\\" could not add TooltipPostCall, and will not function properly\")\n    end\n    ResetInteractables()\nend",
