["displayText"] = "%c",
["customText"] = "-- Chatty Rumormonger - Display\n\nfunction()\n    if not aura_env.config.rumor_helper_enable then\n        return \"\"\n    end\n\n    local locale = GetLocale()\n    local language = string.sub(locale, 1, 2)\n    \n    local aura_env = aura_env\n    local clues_found = aura_env.region.clues_found\n    if WeakAuras.IsOptionsOpen() then\n        -- EXAMPLE DATA\n        clues_found = {[45666]=true,[45675]=true,[45673]=true,[45677]=true,[45636]=true}\n    end\n    if not clues_found or aura_env.region.spy_found then\n        return \"\"\n    end\n\n    local clueCount = 0\n    local clueList = \"\"\n    for gossipOptionID,i in pairs(clues_found) do\n        local rumors = aura_env.rumorIDs[gossipOptionID]\n        if rumors then\n            local clue = rumors[language] or rumors[\"en\"]\n            clueList = clueList..clue..\"\\n\"\n            clueCount = clueCount + 1\n        end\n    end\n    local header = \"Clues Found: \"\n    if clueCount == 0 then\n        return \"\"\n    elseif clueCount == 5 then\n        header  = header..\"|cFF00FF00\"\n    else\n        header  = header..\"|cFFFF0000\"\n    end\n    header = header..clueCount..\"/5|r\"\n    return header..\"\\n--------------------\\n\"..clueList\nend",
["shadowYOffset"] = -1,
["anchorPoint"] = "CENTER",
["displayText_format_p_time_format"] = 0,
["customTextUpdate"] = "event",
["url"] = "https://wago.io/hke42iGI6/24",
["actions"] = {
["start"] = {
["custom"] = "\n\n",
["do_custom"] = false,
},
["init"] = {
["custom"] = "-- Chatty Rumormonger - On Init\n\nlocal aura_env = aura_env\naura_env.AddOnMessagePrefix_COS = \"COS_HELPER\"\naura_env.AddOnMessagePrefix_DBM = \"D5\"\naura_env.AddOnMessagePrefix_BW = \"BigWigs\"\n\naura_env.rumorIDs = {\n    [0] = {\n        en=\"Unknown\"\n    },\n    --dark vest\n    [45635] = {\n        en=\"dark vest\",\n        de=\"dunkle Weste\",\n    },\n    --light vest\n    [45636] = {\n        en=\"light vest\",\n        de=\"helle Weste\",\n    },\n    --male\n    [45657] = {\n        en=\"male\",\n        de=\"mnnlich\",\n    },\n    --female\n    [45658] = {\n        en=\"female\",\n        de=\"weiblich\",\n    },\n    --book\n    [45659] = {\n        en=\"book\",\n        de=\"Buch\",\n    },\n    --pouch\n    [45660] = {\n        en=\"pouch\",\n        de=\"Beutel\",\n    },\n    --potion\n    [45666] = {\n        en=\"potion\",\n        de=\"Trank\",\n    },\n    --no potion\n    [45667] = {\n        en=\"no potion\",\n        de=\"kein Trank\",\n    },\n    --no gloves \n    [45672] = {\n        en=\"no gloves\",\n        de=\"keine Handschuhe\",\n    },\n    --gloves\n    [45673] = {\n        en=\"gloves\",\n        de=\"Handschuhe\",\n    },\n    --cape\n    [45674] = {\n        en=\"cape\",\n        de=\"Umhang\",\n    },\n    --no cape\n    [45675] = {\n        en=\"no cape\",\n        de=\"kein Umhang\",\n    },\n    --long sleeves\n    [45676] = {\n        en=\"long sleeves\",\n        de=\"lange rmel\",\n    },\n    --short sleeves\n    [45677] = {\n        en=\"short sleeves\",\n        de=\"kurze rmel\",\n    },\n}\naura_env.interop_map = {\n    [1] = 45674,\n    [2] = 45675,\n    [3] = 45660,\n    [4] = 45666,\n    [5] = 45676,\n    [6] = 45677,\n    [7] = 45673,\n    [8] = 45672,\n    [9] = 45657,\n    [10] = 45658,\n    [11] = 45636,\n    [12] = 45635,\n    [13] = 45667,\n    [14] = 45659,\n}\naura_env.region.clues_found = {}\naura_env.region.spy_found = false\n\nlocal function ResetClues()\n    aura_env.region.clues_found = {}\n    aura_env.region.spy_found = false\n    print(\"WeakAura \\\"Court of Stars Helper\\\" clues have been reset\")\nend\naura_env.CourtOfStarsHelper_ResetClues = ResetClues\n\nlocal function OnGossipShow()\n    if not aura_env.config.rumor_helper_enable then\n        return\n    end\n\n    local options = C_GossipInfo.GetOptions();\n    \n    local guid = UnitGUID(\"target\")\n    if not guid then\n        return\n    end\n    \n    local _, _, _, _, _, npc_id = strsplit(\"-\", guid)\n    if npc_id ~= \"107486\" then\n        return\n    end\n    \n    if #options < 1 then\n        C_Timer.After(0.3, function() C_GossipInfo.CloseGossip() end)\n        return\n    end\n    \n    local gossipOptionID = options[1].gossipOptionID\n    C_Timer.After(0.1, function() C_GossipInfo.SelectOption(gossipOptionID) end)\n\n    local clue_found = aura_env.region.clues_found[gossipOptionID] or false\n    if clue_found then\n        return false\n    end\n    aura_env.region.clues_found[gossipOptionID] = true\n    \n    local message\n    local rumors = aura_env.rumorIDs[gossipOptionID]\n    if rumors then\n        local locale = GetLocale()\n        local language = string.sub(locale, 1, 2)\n        message = rumors[language] or rumors[\"en\"]\n        if language ~= \"en\" and rumors[language] then\n            message = message .. \" / \" .. rumors[\"en\"]\n        end\n    end\n    \n    local chatType = \"SAY\"\n    if IsInLFGDungeon() then\n        chatType = \"INSTANCE_CHAT\"\n    elseif IsInGroup() then\n        chatType = \"PARTY\"\n    end\n    SendChatMessage(message, chatType)\n\n    WeakAuras.ScanEvents(\"COS_HELPER_CLUE_FOUND\", gossipOptionID)\n    C_ChatInfo.SendAddonMessage(aura_env.AddOnMessagePrefix_COS, gossipOptionID, chatType)\nend\n\nlocal isEnabled = aura_env.config.rumor_helper_enable or true\nlocal isRegistered = aura_env.region.RumorHelperRegistered or false\nlocal frames = {GetFramesRegisteredForEvent(\"GOSSIP_SHOW\")}\nif isEnabled and not isRegistered then\n    if not C_ChatInfo.RegisterAddonMessagePrefix(aura_env.AddOnMessagePrefix_COS) then\n        print(\"Unable to Register COS_HELPER, party sync will not work\")\n    end\n    if aura_env.config.syncDBM and not C_ChatInfo.RegisterAddonMessagePrefix(aura_env.AddOnMessagePrefix_DBM) then\n        print(\"Unable to Register DBM, party sync will not work\")\n    end\n    if aura_env.config.syncBW and not C_ChatInfo.RegisterAddonMessagePrefix(aura_env.AddOnMessagePrefix_BW) then\n        print(\"Unable to Register BigWigs, party sync will not work\")\n    end\n    for _, frame in pairs(frames) do\n        frame:UnregisterEvent(\"GOSSIP_SHOW\")\n    end\n    local rumorHelper = CreateFrame(\"Frame\", \"CourtOfStarsHelper_RumorHelper\")\n    rumorHelper:RegisterEvent(\"GOSSIP_SHOW\")\n    rumorHelper:SetScript(\"OnEvent\", OnGossipShow)\n    aura_env.region.RumorHelperRegistered = true\n    for _, frame in pairs(frames) do\n        if frame.name ~= \"CourtOfStarsHelper_RumorHelper\" then\n            frame:RegisterEvent(\"GOSSIP_SHOW\")\n        end\n    end\n    ResetClues()\nelseif not isEnabled and isRegistered then\n    for _, frame in pairs(frames) do\n        if frame.name == \"CourtOfStarsHelper_RumorHelper\" then\n            frame:UnregisterEvent(\"GOSSIP_SHOW\")\n            break\n        end\n    end\n    aura_env.region.RumorHelperRegistered = false\nend",
