["custom"] = "-- Chatty Rumormonger - Trigger - CHAT_MSG_ADDON, COS_HELPER_CLUE_FOUND\n\nfunction(event, prefix, message)\n    if event == \"COS_HELPER_CLUE_FOUND\" then\n        return true\n    elseif event ~= \"CHAT_MSG_ADDON\" then\n        return true\n    end\n    if not prefix then\n        return false\n    end\n\n    local gossipOptionID = 0\n    if prefix == aura_env.AddOnMessagePrefix_COS then\n        gossipOptionID = tonumber(message) or 0\n    elseif aura_env.config.syncDBM and prefix == aura_env.AddOnMessagePrefix_DBM then\n        local parts = {}\n        for str in string.gmatch(message, \"([^\\t]+)\") do\n                table.insert(parts, str)\n        end\n        local message = parts[#parts-1]\n        local clue = parts[#parts]\n        if message == \"CoS\" then\n            gossipOptionID = aura_env.interop_map[tonumber(clue)] or 0\n        end\n    elseif aura_env.config.syncBW and prefix == aura_env.AddOnMessagePrefix_BW then\n        local parts = {}\n        for str in string.gmatch(message, \"([^^]+)\") do\n                table.insert(parts, str)\n        end\n        local message = parts[#parts-1]\n        local clue = parts[#parts]\n        if message == \"clue\" then\n            gossipOptionID = aura_env.interop_map[tonumber(clue)] or 0\n        end\n    end\n    \n    if gossipOptionID == 0 then\n        return false\n    end\n    \n    local clue_found = aura_env.region.clues_found[gossipOptionID] or false\n    if clue_found then\n        return false\n    end\n    aura_env.region.clues_found[gossipOptionID] = true\n\n    return true\nend",
