["init"] = {
["custom"] = "local MaxActuationPoint = .5 -- 0 for always visible\nlocal duration = .4\nlocal Density = 0.008\nlocal ElementCap = 120\nlocal PastCursorX, PastCursorY, PresentCursorX, PresentCursorY = 0, 0, 0, 0\n\n\naura_env.Trail = function(self)\n    local elapsed\n    \n    if not self.present then\n        self.present = GetTime()\n        elapsed = 0\n    else\n        elapsed = GetTime() - self.present\n        self.present = GetTime()\n    end\n    \n    self.MaxDensity = (self.MaxDensity or 0) + elapsed\n    \n    if self.MaxDensity >= Density then\n        self.MaxDensity = 0\n        \n        PastCursorX = PresentCursorX\n        PastCursorY = PresentCursorY\n        PresentCursorX, PresentCursorY = GetCursorPosition()\n        \n        local x = PresentCursorX - PastCursorX\n        local y = PresentCursorY - PastCursorY\n        local ActuationPoint = math.sqrt(x * x + y * y)\n        \n        if ActuationPoint >= MaxActuationPoint and #self.elements > 0 then\n            --NewElement\n            \n            local element = self.elements[#self.elements]\n            \n            self.elements[#self.elements] = nil\n            self.elementsGenerated[#self.elementsGenerated + 1] = element\n            \n            element.duration = duration\n            \n            local x, y = GetCursorPosition()\n            local Scale = UIParent:GetEffectiveScale()\n            element.x = x / Scale\n            element.y = y / Scale\n            \n            \n            if aura_env.config.TrailSE == 2 then\n                element:SetVertexColor(math.random(), math.random(), math.random())            \n            else\n                \n                if aura_env.config.TrailClassColor then\n                    local c = RAID_CLASS_COLORS[select(2,UnitClass(\"player\"))]\n                    element:SetVertexColor(c.r, c.g, c.b,1)                \n                else\n                    local color = aura_env.config.TrailColor\n                    element:SetVertexColor(color[1], color[2], color[3], color[4])\n                end\n            end\n            \n            \n            element:SetTexture(aura_env.region.texture:GetTexture())\n            element:SetSize(Scale,Scale)\n            element:SetBlendMode(\"ADD\")\n            element:SetPoint(\"CENTER\", UIParent, \"BOTTOMLEFT\", element.x, element.y)\n            element:Show()\n        end\n        \n        -- Update existing elements\n        for i = #self.elementsGenerated, 1, -1 do\n            \n            if aura_env.config.TrailSE == 3 then\n                self.elementsGenerated[i]:SetVertexColor(math.random(), math.random(), math.random())            \n            end\n            \n            self.elementsGenerated[i].duration = self.elementsGenerated[i].duration - elapsed\n            \n            if self.elementsGenerated[i].duration < 0 then\n                self.elementsGenerated[i]:Hide()\n                self.elements[#self.elements + 1] = table.remove(self.elementsGenerated, i)\n            else\n                local ElementScale = math.max(0.1, self.elementsGenerated[i].duration / duration)\n                \n                self.elementsGenerated[i]:SetSize(50 * ElementScale, 50 * ElementScale)\n                self.elementsGenerated[i]:SetPoint(\"CENTER\", UIParent, \"BOTTOMLEFT\", self.elementsGenerated[i].x, self.elementsGenerated[i].y)    \n            end\n            \n        end\n    end\nend\n\n\n--Main Init\nif aura_env.elements == nil then\n    \n    aura_env.elements = {}\n    aura_env.elementsGenerated = {}\n    \n    for i = 1, ElementCap, 1 do\n        aura_env.elements[i] = UIParent:CreateTexture(nil)\n    end\nend",
