["custom"] = "--Default\nlocal texturePath = \"Interface\\\\Addons\\\\WeakAuras\\\\PowerAurasMedia\\\\Auras\\\\Aura45\"\n\n\nif aura_env.config.Crosshair == 1 then\n    --Chevron\n    texturePath = \"uitools-icon-chevron-down\"  \n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 2 then   \n    --Crosshair\n    texturePath = \"uitools-icon-plus\"    \n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 3 then   \n    --Diamond\n    texturePath = \"UF-SoulShard-FX-FrameGlow\"\n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 4 then   \n    --Flatline\n    texturePath = \"uitools-icon-minus\"\n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 5 then   \n    --If glowing Star\n    texturePath = \"AftLevelup-WhiteStarBurst\"\n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 6 then \n    texturePath = \"Interface\\\\Addons\\\\WeakAuras\\\\PowerAurasMedia\\\\Auras\\\\Aura72\"\n    aura_env.region:SetScale(.5)\n    \nelseif aura_env.config.Crosshair == 7 then   \n    --Ring\n    texturePath = \"Interface\\\\AddOns\\\\WeakAuras\\\\Media\\\\Textures\\\\circle_border5.tga\"\n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 8 then   \n    --Tech Arrow\n    texturePath = \"ProgLan-w-4\" \n    aura_env.region:SetScale(1)\n    \nelseif aura_env.config.Crosshair == 9 then   \n    --X\n    texturePath = \"uitools-icon-close\"    \n    aura_env.region:SetScale(1)\n    \nend\n\n--Elite\n--texturePath = \"Rare-Elite-Star\"\n\n--Solid Diamond\n--texturePath = \"UF-SoulShard-FX-HolderGlow\"\n\n\naura_env.region:SetTexture(texturePath)\n--aura_env.region:SetScale(.5)\n--aura_env.region:SetWidth(10)\n--aura_env.region:SetHeight(10)\n\n\n\n\n\n\n",
