["custom"] = "aura_env.yellow = {r=1, g=0.96, b=0.41}\naura_env.orange = {r=1, g=0.49, b=0.04}\naura_env.purple = {r=0.745, g=0.231, b=0.808}\naura_env.green = {r=0.19, g=0.78, b=0.18}\naura_env.silver = {r=0.612, g=0.702, b=0.749}\naura_env.blue = {r=0.25, g=0.78, b=0.92}\n\naura_env.grey = {r=0.15, g=0.15, b=0.15, a=1}\naura_env.black = {r=0.05, g=0.05, b=0.05, a=1}\naura_env.white = {r=0.8, g=0.8, b=0.8, a=1}\n\n---------------------------------------------------------------------------------------------------\n\naura_env.onInit = function()\n    aura_env.createRing(0, aura_env.grey, false, nil)\n    local ring1 = aura_env.createRing(1, aura_env.grey, true, aura_env.yellow)\n    local ring2 = aura_env.createRing(2, aura_env.grey, true, aura_env.orange)\n    local ring3 = aura_env.createRing(3, aura_env.grey, true, aura_env.purple)\n    local ring4 = aura_env.createRing(4, aura_env.grey, true, aura_env.green)\n    local ring5 = aura_env.createRing(5, aura_env.grey, true, aura_env.silver)\n    local ring6 = aura_env.createRing(6, aura_env.grey, true, aura_env.blue)\n    \n    aura_env.rings = {ring1, ring2, ring3, ring4, ring5, ring6}\nend\n\n---------------------------------------------------------------------------------------------------\n\naura_env.highlightRing = function(index)\n    aura_env.clearAllHighlights()\n    aura_env.rings[index]:Show()\nend\n\n\naura_env.clearAllHighlights = function()\n    for _, frame in ipairs(aura_env.rings) do\n        frame:Hide()\n    end\nend\n\n---------------------------------------------------------------------------------------------------\n\naura_env.createRing = function(index, backdropColor, showLabel, highlightColor)\n    local frameLevel = 500-(index*5)\n    local diameter = (index+1)*30\n    \n    aura_env.createCircle(diameter, aura_env.silver, frameLevel)\n    aura_env.createCircle(diameter-2, backdropColor, frameLevel+1)\n    \n    if (highlightColor ~= nil) then\n        local labelOffset = index*15+7\n        aura_env.createLabel(index, labelOffset, aura_env.white)\n        \n        local highlightedRing = aura_env.createCircle(diameter-2, highlightColor, frameLevel+2)\n        aura_env.animateFrame(highlightedRing)\n        highlightedRing:Hide()\n        \n        return highlightedRing\n    end\nend\n\n\naura_env.createCircle = function(diameter, color, frameLevel)\n    local frame = CreateFrame(\"Frame\", nil, aura_env.region)\n    \n    frame:SetPoint(\"CENTER\", aura_env.region)\n    frame:SetSize(diameter, diameter)\n    frame:SetFrameLevel(frameLevel)\n    \n    frame.texture = frame:CreateTexture(nil, \"ARTWORK\")\n    frame.texture:SetTexture(\"Interface\\\\AddOns\\\\WeakAuras\\\\Media\\\\Textures\\\\Circle_White.tga\")\n    frame.texture:SetVertexColor(color.r, color.g, color.b, color.a)\n    frame.texture:SetAllPoints(frame)\n    \n    return frame\nend\n\n\naura_env.animateFrame = function(frame)\n    local ag = frame:CreateAnimationGroup()\n    \n    local fadeOut = ag:CreateAnimation(\"Alpha\")\n    fadeOut:SetFromAlpha(1)\n    fadeOut:SetToAlpha(0.4)\n    fadeOut:SetDuration(0.4)\n    fadeOut:SetOrder(1)\n    fadeOut:SetSmoothing(\"IN_OUT\")\n    \n    local fadeIn = ag:CreateAnimation(\"Alpha\")\n    fadeIn:SetFromAlpha(0.4)\n    fadeIn:SetToAlpha(1)\n    fadeIn:SetDuration(0.4)\n    fadeIn:SetOrder(2)\n    fadeIn:SetSmoothing(\"IN_OUT\")\n    \n    ag:SetLooping(\"REPEAT\")\n    ag:Play()\nend\n\n\naura_env.createLabel = function(label, offset, color)\n    local frame = CreateFrame(\"Frame\", nil, aura_env.region)\n    frame:SetPoint(\"CENTER\", aura_env.region)\n    frame:SetSize(1, 1)\n    frame:SetFrameLevel(900)\n    \n    local fontString = frame:CreateFontString(frame, \"OVERLAY\", \"GameTooltipText\")\n    fontString:SetPoint(\"CENTER\", offset, 0)\n    fontString:SetText(label)\n    fontString:SetTextColor(color.r, color.g, color.b, color.a)\nend\n\n---------------------------------------------------------------------------------------------------\n\naura_env.onInit()\n\n\n",
