["init"] = {
["custom"] = "-- Bar Logic by Potato\nlocal aura_env = aura_env\naura_env.this = aura_env.region;\n\naura_env.region.bar.fg:SetGradient(\"HORIZONTAL\",0.07,0.49,0.82,0.29,0.76,1.00)\n\n-- Options\naura_env.showPrediction = true; -- predictive Maelstrom text\naura_env.predictStyle = 2; -- 1 = currentMael + incoming, 2 = Mael After Cast-- cutaway \naura_env.predictWidth = 1; -- width of casting spark for prediction\naura_env.predictAlpha = 0; -- alpha of casting spark on prediction\naura_env.showSpendAnimation = true; -- enable fade/cutaway feature\naura_env.cutawayTimer = 0.122; -- time for complete animation\naura_env.sparkTexture = [[Interface\\AddOns\\WeakAuras\\Media\\Textures\\Square_White]];\naura_env.showCutaway = false; -- true for the slide and fade, false for fade only\naura_env.cutawayOffset = aura_env.region.bar:GetHeight() / 2; -- offset to cut if not on fade only\naura_env.showEarthshockCost = false; -- show a line on the bar for earth shock\naura_env.earthshockCostWidth = 1; -- width of line on the bar\n\n-- General\naura_env.frame = _G[\"wa-elemental-frame\"] or CreateFrame(\"Frame\", \"wa-elemental-frame\")\naura_env.frame:SetParent(aura_env.this)\naura_env.frame:SetFrameStrata(\"LOW\")\naura_env.asp = {UnitPower(\"player\", Enum.PowerType.Maelstrom), UnitPowerMax(\"player\", Enum.PowerType.Maelstrom)};    \naura_env.powerPerPixel = aura_env.asp[2] / aura_env.this:GetWidth();\n\n\nlocal a = aura_env.region\naura_env.text = _G[\"wa-elemental-fs\"] or aura_env.frame:CreateFontString(\"wa-elemental-fs\", \"OVERLAY\")\naura_env.text:SetJustifyH(\"CENTER\")\naura_env.text:SetJustifyV(\"MIDDLE\")\naura_env.text:SetPoint(\"CENTER\", a.bar, \"CENTER\", 0, 0);\naura_env.text:SetFont(a.text:GetFont())\naura_env.text:SetTextColor(a.text:GetTextColor())\naura_env.text:SetShadowOffset(1, -1)\naura_env.text:Show()\naura_env.updatePowerText = function()    \n    if aura_env.inc then\n        local after = aura_env.asp[1] + aura_env.inc;\n        if aura_env.predictStyle == 1 then            \n            aura_env.text:SetText(format(\"%i \\124cff%s+ %i\\124r\", aura_env.asp[1], after <= aura_env.asp[2] and \"00ff00\" or \"ff0000\", aura_env.inc));            \n        else\n            aura_env.text:SetText(format(\"\\124cff%s%s%i%s\\124r\", after <= aura_env.asp[2] and \"ffffff\" or \"ff0000\", after > aura_env.asp[2] and \"\" or \"\", after, after > aura_env.asp[2] and \"\" or \"\")); \n        end \n    else\n        aura_env.text:SetText(aura_env.asp[1]);\n    end\nend\naura_env.updatePowerText();\n\n-- Predictive Casting\naura_env.generators = \n{\n    [188196] = 8,\n    [51505] = 10,\n    [117014] = 30,\n    [210714] = 25  \n}\n-- Casting Spark\nif aura_env.showPrediction then\n    aura_env.castProgress = 0\n    aura_env.predictCastBar = _G[\"elemental-predict-cast\"] or aura_env.frame:CreateTexture(\"elemental-predict-cast\", \"OVERLAY\");\n    aura_env.predictCastBar:SetWidth(aura_env.predictWidth);\n    aura_env.predictCastBar:SetHeight(aura_env.this:GetHeight() + 0.2);\n    aura_env.predictCastBar:SetPoint(\"LEFT\",aura_env.this.bar,\"RIGHT\", aura_env.castProgress, 0);\n    aura_env.predictCastBar:SetBlendMode(\"ADD\");\n    aura_env.predictCastBar:SetAlpha(aura_env.predictAlpha);\n    if not aura_env.sparkTexture then\n        aura_env.predictCastBar:SetColorTexture(1,1,1,1);\n    else\n        aura_env.predictCastBar:SetTexture(aura_env.sparkTexture);\n    end\n    aura_env.predictCastBar:Hide();\nend\n\n-- Cutaway/Fade Animation\nif aura_env.showSpendAnimation then\n    aura_env.cutaway = _G[\"wa-aspcutaway\"] or CreateFrame(\"StatusBar\", \"wa-aspcutaway\", aura_env.this)\n    aura_env.cutaway:SetHeight(aura_env.this:GetHeight())\n    aura_env.cutaway:SetPoint(\"LEFT\",aura_env.this.bar.fg,\"RIGHT\")\n    aura_env.cutaway:SetFrameStrata(\"MEDIUM\")\n    aura_env.cutaway:SetStatusBarTexture(aura_env.this.bar:GetStatusBarTexture());\n    aura_env.cutaway:Hide();\nelse\n    local r = _G[\"wa-aspcutaway\"];\n    if r then \n        r:Hide();\n    end    \nend\n\nif aura_env.showEarthshockCost then\n    aura_env.earthshock = _G[\"wa-earthshock\"] or aura_env.frame:CreateTexture(\"wa-earthshock\", \"OVERLAY\")\n    aura_env.earthshock:SetSize(aura_env.earthshockCostWidth, aura_env.this:GetHeight()+1)\n    aura_env.earthshock:SetTexture([[Interface\\AddOns\\WeakAuras\\Media\\Textures\\Square_White]])\n    aura_env.earthshock:SetVertexColor(0,0,0,1);\n    aura_env.earthshock:Show();\n    \n    local cost = GetSpellPowerCost(78674)[1].cost;\n    local barW = aura_env.this:GetWidth();   \n    local max = aura_env.asp[2];\n    --local powerPerPix = aura_env.asp[2] / barW;\n    local powerPerPix = barW / aura_env.asp[2];\n    local offset = cost * powerPerPix;\n    aura_env.earthshock:SetPoint(\"CENTER\", aura_env.this, \"LEFT\", offset, 0);\n    \nelse\n    local r = _G[\"wa-earthshock\"];\n    if r then \n        r:Hide();\n    end    \nend",
