["custom"] = "function(event, ...)\n    local arg1, _, arg3 = ...;\n    \n    -- Encounter start resets mana count\n    if event == \"ENCOUNTER_START\" then\n        if arg3 >= 14 and arg3 <= 17 then \n            aura_env.manacost = aura_env.startMana\n        end\n    end\n    \n    -- Making sure to not reset mana if the user reloads UI\n    if event == \"PLAYER_ENTERING_WORLD\" then\n        local isInitialLogin, isReloadingUi = ...;\n        if isReloadingUi then\n            -- Reloading\n            aura_env.manacost = aura_env.savedVariables.highTide\n            return\n        end\n        if not isReloadingUi and not isInitialLogin then\n            -- Switching Zone\n            aura_env.manacost = aura_env.startMana\n        end\n    end\n    \n    -- Reset mana on lvl 100 talent swap or spec change\n    if event == \"PLAYER_SPECIALIZATION_CHANGED\" then\n        if GetTime() == aura_env.timeStamp then\n            aura_env.manacost = aura_env.startMana\n        end\n        aura_env.timeStamp = GetTime()\n    end\n    \n    -- Mana handling\n    if event == \"UNIT_SPELLCAST_SUCCEEDED\" and arg1 == \"player\" then\n        local costTable = GetSpellPowerCost(arg3);\n        if costTable then\n            for _, costInfo in pairs(costTable) do\n                if (costInfo.type == UnitPowerType(\"player\")) then\n                    local spellCost = costInfo.cost\n                    if spellCost == 0 then\n                        -- Check if MTT legendary or leech pot buff is active and invalidate mana\n                        if (arg3 == 1064 or arg3 == 77472) and (WA_GetUnitBuff(\"player\", 335892) or WA_GetUnitBuff(\"player\", 322302)) then\n                            return false\n                        else\n                            spellCost = (costInfo.costPercent / 100 * aura_env.basemana)\n                        end\n                    end\n                    aura_env.manacost = aura_env.config[\"inverted\"] and (aura_env.manacost - spellCost) or (aura_env.manacost + spellCost)\n                    break\n                end\n            end\n            if aura_env.config[\"inverted\"] and (aura_env.manacost <= aura_env.triggerMana) then\n                aura_env.manacost = aura_env.startMana + aura_env.manacost\n            else if (aura_env.manacost >= aura_env.triggerMana) then\n                    aura_env.manacost = aura_env.manacost - aura_env.triggerMana\n                end\n            end\n        end\n    end\n    \n    aura_env.savedVariables.highTide = aura_env.manacost\n    -- Update Custom Text via Trigger Update\n    return true\nend",
