["custom"] = "-- Hate how hacky this is but it works\nif GetTime() == aura_env.timeStamp then\n    aura_env.manacost = aura_env.startMana\nend\n\n-- Timestamp for detecting aura load on talent swap\naura_env.timeStamp = GetTime()",
