["custom"] = "-- Created by Niseko\n\nlocal playerLevel = UnitLevel(\"player\") \n\nif playerLevel < 60 then -- Prepatch\n    aura_env.basemana = 2000\nelseif playerLevel < 120 then -- SL\n    aura_env.basemana = 10000\nelse -- BFA\n    aura_env.basemana = 20000\nend\nlocal triggerAmount = aura_env.basemana * 2\n\naura_env.startMana = aura_env.config[\"inverted\"] and triggerAmount or 0\naura_env.triggerMana = aura_env.config[\"inverted\"] and 0 or triggerAmount\naura_env.manacost = aura_env.startMana\n\naura_env.savedVariables = WeakAurasSaved.displays[aura_env.id]\nif not aura_env.savedVariables.highTide then\n    aura_env.savedVariables.highTide = aura_env.startMana\nend\n\naura_env.ShortenNumber = function(value)\n    if not value then return end\n    local function round(val, decimal)\n        if (decimal) then\n            return math.floor( (val * 10^decimal) + 0.5) / (10^decimal)\n        else\n            return math.floor(val+0.5)\n        end\n    end\n    if(value > 9999) then return round(value/1000, 0) .. 'K' end\n    if(value > 999) then return round(value/1000, 1) .. 'K' end\n    return value\nend",
